// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract CharityDonation {
    struct Donation {
        address donor;
        address recipient;
        uint256 amount;
        uint256 timestamp;
    }

    IERC20 public stablecoin;
    Donation[] public donations;
    mapping(address => uint256) public charityBalances;

    constructor(address _stablecoin) {
        stablecoin = IERC20(_stablecoin);
    }

    function donate(address recipient, uint256 amount) external {
        require(amount > 0, "Amount must be > 0");
        require(stablecoin.transferFrom(msg.sender, address(this), amount), "Transfer failed");

        charityBalances[recipient] += amount;
        donations.push(Donation(msg.sender, recipient, amount, block.timestamp));
    }

    function withdraw(uint256 amount) external {
        require(charityBalances[msg.sender] >= amount, "Not enough balance");
        charityBalances[msg.sender] -= amount;
        require(stablecoin.transfer(msg.sender, amount), "Withdraw failed");
    }

    function getDonationHistory() external view returns (Donation[] memory) {
        return donations;
    }
}

