contracts/RemittanceContract.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

interface ISwapRouter {
    function swapExactTokensForTokens(
        uint amountIn,
        uint amountOutMin,
        address[] calldata path,
        address to,
        uint deadline
    ) external returns (uint[] memory amounts);
    
    function getAmountsOut(
        uint amountIn, 
        address[] calldata path
    ) external view returns (uint[] memory amounts);
}

contract RemittanceContract is Ownable, ReentrancyGuard {
    ISwapRouter public swapRouter;
    address public stablecoin; // e.g., XSGD address
    
    // Storing remittance details
    struct Remittance {
        address sender;
        address recipient;
        address sourceToken;
        uint256 sourceAmount;
        address targetToken;
        uint256 targetAmount;
        uint8 status; // 0: pending, 1: processing, 2: completed, 3: failed
        uint256 timestamp;
    }
    
    mapping(uint256 => Remittance) public remittances;
    uint256 public remittanceCounter;
    
    // Fee structure
    uint256 public platformFeePercentage = 50; // 0.5% (in basis points)
    uint256 public constant MAX_FEE = 500; // 5% maximum fee
    
    // Pause mechanism
    bool public paused;
    
    // Events
    event RemittanceSent(
        uint256 indexed remittanceId,
        address indexed sender,
        address indexed recipient,
        address sourceToken,
        uint256 sourceAmount,
        address targetToken,
        uint256 targetAmount,
        uint256 timestamp
    );
    
    event RemittanceStatusUpdated(
        uint256 indexed remittanceId,
        uint8 status
    );
    
    event FeeUpdated(uint256 newFeePercentage);
    event RouterUpdated(address newRouter);
    event StablecoinUpdated(address newStablecoin);
    event Paused();
    event Unpaused();
    
    // Modifiers
    modifier whenNotPaused() {
        require(!paused, "Contract is paused");
        _;
    }
    
    constructor(address _swapRouter, address _stablecoin) Ownable(msg.sender) {
        require(_swapRouter != address(0), "Invalid router address");
        require(_stablecoin != address(0), "Invalid stablecoin address");
        swapRouter = ISwapRouter(_swapRouter);
        stablecoin = _stablecoin;
    }
    
    /**
     * @dev Get a quote for converting a token to stablecoin
     * @param sourceToken The token to convert from
     * @param amount The amount to convert
     * @return The estimated amount of stablecoin to receive
     */
    function getConversionQuote(
        address sourceToken,
        uint256 amount
    ) external view returns (uint256) {
        if (sourceToken == stablecoin) {
            return amount;
        }
        
        address[] memory path = new address[](2);
        path[0] = sourceToken;
        path[1] = stablecoin;
        
        try swapRouter.getAmountsOut(amount, path) returns (uint[] memory amounts) {
            return amounts[1];
        } catch {
            revert("Failed to get quote");
        }
    }
    
    /**
     * @dev Calculate the platform fee for a given amount
     * @param amount The amount to calculate fee on
     * @return The fee amount
     */
    function calculateFee(uint256 amount) public view returns (uint256) {
        return (amount * platformFeePercentage) / 10000;
    }
    
    /**
     * @dev Send tokens with conversion to stablecoin in a single transaction
     * @param sourceToken The token to send
     * @param amount The amount to send
     * @param recipient The recipient address
     * @return remittanceId The ID of the created remittance
     */
    function sendWithConversion(
        address sourceToken,
        uint256 amount,
        address recipient
    ) external nonReentrant whenNotPaused returns (uint256) {
        require(sourceToken != address(0), "Invalid source token");
        require(amount > 0, "Amount must be greater than 0");
        require(recipient != address(0), "Invalid recipient address");
        
        // Create a new remittance ID
        uint256 remittanceId = remittanceCounter++;
        
        // Set initial status to processing
        remittances[remittanceId] = Remittance({
            sender: msg.sender,
            recipient: recipient,
            sourceToken: sourceToken,
            sourceAmount: amount,
            targetToken: stablecoin,
            targetAmount: 0, // Will be updated after swap
            status: 1, // Processing
            timestamp: block.timestamp
        });
        
        // Transfer tokens from sender to this contract
        IERC20(sourceToken).transferFrom(msg.sender, address(this), amount);
        
        uint256 targetAmount;
        
        // If the token is already the stablecoin, just send it directly
        if (sourceToken == stablecoin) {
            uint256 fee = calculateFee(amount);
            targetAmount = amount - fee;
            
            // Send stablecoins to recipient
            IERC20(stablecoin).transfer(recipient, targetAmount);
        } else {
            // Approve router to spend tokens
            IERC20(sourceToken).approve(address(swapRouter), amount);
            
            // Prepare path for swap (token -> stablecoin)
            address[] memory path = new address[](2);
            path[0] = sourceToken;
            path[1] = stablecoin;
            
            // Swap tokens to stablecoin
            try swapRouter.swapExactTokensForTokens(
                amount,
                1, // Min amount out (1 wei to prevent reverting on small slippage)
                path,
                address(this),
                block.timestamp + 300 // 5 minute deadline
            ) returns (uint[] memory amounts) {
                targetAmount = amounts[1];
                uint256 fee = calculateFee(targetAmount);
                targetAmount = targetAmount - fee;
                
                // Send stablecoins to recipient
                IERC20(stablecoin).transfer(recipient, targetAmount);
            } catch {
                // Update remittance status to failed
                remittances[remittanceId].status = 3; // Failed
                emit RemittanceStatusUpdated(remittanceId, 3);
                
                // Refund source tokens to sender
                IERC20(sourceToken).transfer(msg.sender, amount);
                
                revert("Swap failed");
            }
        }
        
        // Update remittance data with final amounts and status
        remittances[remittanceId].targetAmount = targetAmount;
        remittances[remittanceId].status = 2; // Completed
        
        emit RemittanceSent(
            remittanceId,
            msg.sender,
            recipient,
            sourceToken,
            amount,
            stablecoin,
            targetAmount,
            block.timestamp
        );
        
        emit RemittanceStatusUpdated(remittanceId, 2);
        
        return remittanceId;
    }
    
    /**
     * @dev Send tokens with conversion and slippage protection
     * @param sourceToken The token to send
     * @param amount The amount to send
     * @param recipient The recipient address
     * @param minAmountOut The minimum amount of stablecoin to receive
     * @return remittanceId The ID of the created remittance
     */
    function sendWithConversionAndSlippage(
        address sourceToken,
        uint256 amount,
        address recipient,
        uint256 minAmountOut
    ) external nonReentrant whenNotPaused returns (uint256) {
        require(sourceToken != address(0), "Invalid source token");
        require(amount > 0, "Amount must be greater than 0");
        require(recipient != address(0), "Invalid recipient address");
        
        // Create a new remittance ID
        uint256 remittanceId = remittanceCounter++;
        
        // Set initial status to processing
        remittances[remittanceId] = Remittance({
            sender: msg.sender,
            recipient: recipient,
            sourceToken: sourceToken,
            sourceAmount: amount,
            targetToken: stablecoin,
            targetAmount: 0, // Will be updated after swap
            status: 1, // Processing
            timestamp: block.timestamp
        });
        
        // Transfer tokens from sender to this contract
        IERC20(sourceToken).transferFrom(msg.sender, address(this), amount);
        
        uint256 targetAmount;
        
        // If the token is already the stablecoin, just send it directly
        if (sourceToken == stablecoin) {
            uint256 fee = calculateFee(amount);
            targetAmount = amount - fee;
            
            require(targetAmount >= minAmountOut, "Slippage too high");
            
            // Send stablecoins to recipient
            IERC20(stablecoin).transfer(recipient, targetAmount);
        } else {
            // Approve router to spend tokens
            IERC20(sourceToken).approve(address(swapRouter), amount);
            
            // Prepare path for swap (token -> stablecoin)
            address[] memory path = new address[](2);
            path[0] = sourceToken;
            path[1] = stablecoin;
            
            // Swap tokens to stablecoin with slippage protection
            try swapRouter.swapExactTokensForTokens(
                amount,
                minAmountOut,
                path,
                address(this),
                block.timestamp + 300 // 5 minute deadline
            ) returns (uint[] memory amounts) {
                targetAmount = amounts[1];
                uint256 fee = calculateFee(targetAmount);
                targetAmount = targetAmount - fee;
                
                // Send stablecoins to recipient
                IERC20(stablecoin).transfer(recipient, targetAmount);
            } catch {
                // Update remittance status to failed
                remittances[remittanceId].status = 3; // Failed
                emit RemittanceStatusUpdated(remittanceId, 3);
                
                // Refund source tokens to sender
                IERC20(sourceToken).transfer(msg.sender, amount);
                
                revert("Swap failed or slippage too high");
            }
        }
        
        // Update remittance data with final amounts and status
        remittances[remittanceId].targetAmount = targetAmount;
        remittances[remittanceId].status = 2; // Completed
        
        emit RemittanceSent(
            remittanceId,
            msg.sender,
            recipient,
            sourceToken,
            amount,
            stablecoin,
            targetAmount,
            block.timestamp
        );
        
        emit RemittanceStatusUpdated(remittanceId, 2);
        
        return remittanceId;
    }
    
    /**
     * @dev Get remittance details by ID
     * @param remittanceId The ID of the remittance
     * @return The remittance struct
     */
    function getRemittance(uint256 remittanceId) external view returns (Remittance memory) {
        require(remittanceId < remittanceCounter, "Remittance does not exist");
        return remittances[remittanceId];
    }
    
    /**
     * @dev Get remittance status by ID
     * @param remittanceId The ID of the remittance
     * @return The status of the remittance
     */
    function getRemittanceStatus(uint256 remittanceId) external view returns (uint8) {
        require(remittanceId < remittanceCounter, "Remittance does not exist");
        return remittances[remittanceId].status;
    }
    
    // Admin functions
    
    /**
     * @dev Set the platform fee percentage (in basis points)
     * @param _feePercentage The new fee percentage
     */
    function setPlatformFee(uint256 _feePercentage) external onlyOwner {
        require(_feePercentage <= MAX_FEE, "Fee too high");
        platformFeePercentage = _feePercentage;
        emit FeeUpdated(_feePercentage);
    }
    
    /**
     * @dev Set the swap router address
     * @param _swapRouter The new swap router address
     */
    function setSwapRouter(address _swapRouter) external onlyOwner {
        require(_swapRouter != address(0), "Invalid router address");
        swapRouter = ISwapRouter(_swapRouter);
        emit RouterUpdated(_swapRouter);
    }
    
    /**
     * @dev Set the stablecoin address
     * @param _stablecoin The new stablecoin address
     */
    function setStablecoin(address _stablecoin) external onlyOwner {
        require(_stablecoin != address(0), "Invalid stablecoin address");
        stablecoin = _stablecoin;
        emit StablecoinUpdated(_stablecoin);
    }
    
    /**
     * @dev Pause the contract
     */
    function pause() external onlyOwner {
        paused = true;
        emit Paused();
    }
    
    /**
     * @dev Unpause the contract
     */
    function unpause() external onlyOwner {
        paused = false;
        emit Unpaused();
    }
    
    /**
     * @dev Withdraw accumulated fees
     * @param token The token to withdraw
     */
    function withdrawFees(address token) external onlyOwner {
        uint256 balance = IERC20(token).balanceOf(address(this));
        require(balance > 0, "No balance to withdraw");
        IERC20(token).transfer(owner(), balance);
    }
}

